const elCopyBtn=document.getElementById("copy-btn"),elColorText=document.getElementById("color-text"),elColorInput=document.getElementById("color-input"),span=elCopyBtn.querySelector("span");elColorInput.addEventListener("input",({target:e})=>{let{value:t}=e;elColorText.textContent=t,document.body.style.backgroundColor=t}),elCopyBtn.addEventListener("click",async()=>{elCopyBtn.disabled=!0;try{await navigator.clipboard.writeText(elColorText.textContent),span.innerText="Copied!"}catch{span.innerText="Not copied!"}setTimeout(()=>{span.innerText="Copy",elCopyBtn.disabled=!1},1500)});